public class PowerOfTwo {

    public static void main(String[] args) {
        String A = "";
        int output = PowerOfTwo.power(A);
        System.out.println(output);
    }

    public static int power(String A) {

        if ((A.charAt(A.length() - 1) - '0') % 2 != 0) {
            return 0;
        }
        int ans = 0;
        int remainder = 0;
        String newString = "";
        while (A.length() != 1 || (A.charAt(A.length() - 1) - '0') % 2 == 0) {
            for (int i = 0; i < A.length(); i++) {
                int value = (A.charAt(i) - '0');
                if (remainder == 1) {
                    value += 10;
                    remainder = 0;
                }
                ans = value / 2;
                remainder = value % 2;
                if (newString == "" && ans == 0)
                    continue;
                newString = newString + Integer.toString(ans);
            }
            A = newString;
            newString = "";
            remainder = 0;
            ans = 0;

        }

        if (A.length() == 1 && (A.charAt(0) - '0') % 2 == 0)
            return 1;
        return 0;

    }
}
